const [hourHand, minuteHand, secondHand, digitalTime] = 
      ['hour-hand', 'minute-hand', 'second-hand', 'digital-time'].map(id => document.getElementById(id));
function updateClock() {
    const now = new Date(), h = now.getHours() % 12, m = now.getMinutes(), s = now.getSeconds();
    hourHand.style.transform = `rotate(${h * 30 + m * 0.5 + 90}deg)`;
    minuteHand.style.transform = `rotate(${m * 6 + 90}deg)`;
    secondHand.style.transform = `rotate(${s * 6 + 90}deg)`;
    digitalTime.textContent = [h, m, s].map(n => String(n).padStart(2, '0')).join(':');
}
setInterval(updateClock, 1000); updateClock();
